<?php// If uninstall is not called from WordPress, exitif ( !defined( 'WP_UNINSTALL_PLUGIN' ) ) {	exit ();}global $wpdb;$bots_table = $wpdb->prefix . "name_redactor_bots";$opt_in_opt_out_table = $wpdb->prefix . "name_redactor_opt_in_opt_out"; // Delete tables:$wpdb->query("DROP TABLE IF EXISTS $bots_table");$wpdb->query("DROP TABLE IF EXISTS $opt_in_opt_out_table");// Delete any options:if ( get_option( 'name_redactor_options' ) != false ) {	delete_option( 'name_redactor_options' );}